mapper.select:
SELECT 

FROM equity_buy a

WHERE a.id = #{id}

SELECT
FROM equity_buy a JOIN sys_user u ON u.id=a.buy_userid
where a.buy_userid=#{buyUserid}
AND u.login_name LIKE '%'||#{loginName}||'%' '%'+#{loginName}+'%' CONCAT('%', #{loginName}, '%')
ORDER BY ${page.orderBy}
ORDER BY a.create_date desc

SELECT 

FROM equity_buy a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE equity_sell b SET 	
	b.buy_num = buy_num + #{buyNum}

WHERE b.id = #{equitySellId}

UPDATE sys_user s SET 	
	s.gwf = gwf - #{buyNum} * #{buyMoney} ,
	s.happyfood = happyfood + #{buyNum}
WHERE id = #{buyUserid}
